import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function HOOKS() {

    const [user,setUser] = useState({name:'',email:'',password:''});
    const {name,email,password} = user;

  const handleChange = (e) =>{
    const userDetails = e.target.name;
    if(userDetails === 'name'){
      setUser({name: e.target.name,email,password});
    }else if(userDetails === 'email'){
      setUser({name,email:e.target.value,password});
    }else if(userDetails === 'password'){
      setUser({name,email,password:e.target.password});
    }
  }

  const formHandler = (e) =>{
       console.log("submitted");
       e.preventDefault();
       console.log(user);
  }



  return (
     <div>
      <div className='row'>
      <div className='col-md-6 offset-3 p-3'>
      <Form action=""  onSubmit={formHandler}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>Name</Form.Label>
      <Form.Control type="text" value={name} placeholder="Enter name" required onChange={handleChange} />
    </Form.Group>
    <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>Email address</Form.Label>
      <Form.Control type="email" value={email}  placeholder="Enter email" required onChange={handleChange} />
    </Form.Group>

    <Form.Group className="mb-3" controlId="formBasicPassword">
      <Form.Label>Password</Form.Label>
      <Form.Control type="password" value={password} placeholder="Password" required  onChange={handleChange} />
    </Form.Group>
    <Button variant="primary" type="submit">
      Register
    </Button>
  </Form>
        </div>
      </div>
     </div>

  )
}
