      setName(e.target.value)
      if(name.length < 2 ){
        setInvalid(false);
      }else{
        setInvalid
      }
    }
  return (
    <div>
        <form>
             <input name="text" value={name} onChange={changeValue} />
           
        </form>
    </div>
  )
}

export default InputForm;
