import React, { Component } from 'react'

export default class Conditional_Rendering extends Component {
      constructor(props){
        super(props)

        this.state = {
            changeValue : ''
        }
      }
    onChangeHandler = (e) =>{
         this.setState({
            changeValue : e.target.value
         })
    }

  render() {
   return(
        <div>  
             <input  onChange={this.onChangeHandler} type="text" name="" id="" placeholder=""/>
             <p>{e.target.value}</p>
        </div>
   )
     
}
}
