import React, { Component } from 'react'
import Contact from './Contact'
import Home from './Home'

export default class ConditionalRendering extends Component {
    constructor(props){
        super(props)
        this.state = {
            isLoggedIn : true
        }
    }
  render() {
    const {isLoggedIn} = this.state
    return (
      <div>
         {/* {isLoggedIn === true ? <Home/> : <Contact/>} */}
         if(isLoggedIn==true){
             <Home/> 
         }else{}
      </div>
    )
  }
}
