import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function HOOKS() {

    const [user,setUser] = useState({name:'',email:'',password:''});
    const {name,email,password} = user;

  const handleChange = (e) =>{
    setUser({...user, [e.target.name] : e.target.value});
  }

  const formHandler = (e) =>{
        console.log(user);
       e.preventDefault();

  }



  return (
     <div>
      <div className='row'>
      <div className='col-md-6 offset-3 p-3'>
      <Form action=""  onSubmit={formHandler}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>Name</Form.Label>
      <Form.Control type="text" name="name" value={name} placeholder="Enter name" required onChange={handleChange} />
    </Form.Group>
    <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>Email address</Form.Label>
      <Form.Control type="email" name="email" value={email}  placeholder="Enter email" required onChange={handleChange} />
    </Form.Group>

    <Form.Group className="mb-3" controlId="formBasicPassword">
      <Form.Label>Password</Form.Label>
      <Form.Control type="password" name="password"value={password} placeholder="Password" required  onChange={handleChange} />
    </Form.Group>
    <Button variant="primary" type="submit">
      Register
    </Button>
  </Form>
        </div>
      </div>
     </div>

  )
}
